def isCompletedProperty = context.expand( '${#TestCase#isCompleted}' )
if (isCompletedProperty == 'f')
{
    testRunner.testCase.setPropertyValue( "isCompleted", 'false' )
}
else
{
   testRunner.testCase.setPropertyValue( "isCompleted", 'true' )
}

