import groovy.json.JsonSlurper
import groovy.xml.XmlSlurper

def piListResponse = context.expand('${PIList#Response}')
PIList = new XmlSlurper().parseText(piListResponse)

prescriptionLists = PIList.Body.piListResponse.prescriptionList
count=0
for (int i = 0;i<prescriptionLists.size();i++){
	if(prescriptionLists[i].state!= "expired" && prescriptionLists[i].state!= "completed"){
		count+=1
	}
}
testRunner.TestCase.setPropertyValue("count",count.toString())

