import groovy.json.JsonSlurper

def healthInformationResponse = context.expand('${POST healthInformation#Response}')
def slurperJson = new JsonSlurper()
def healthInformation = slurperJson.parseText(healthInformationResponse)
dispensary = healthInformation.payload.dispensaryGroup

for(i=0; i<dispensary.size(); i++){
	if(dispensary[i].doctor.specialityName != null){
		testRunner.fail("Вернулся specialityName со значением " + dispensary[i].doctor.specialityName)
	}
	if(dispensary[i].doctor.mu.name != null){
		testRunner.fail("Вернулся mu.name со значением " + dispensary[i].doctor.mu.name)
	}
}

