import groovy.json.JsonSlurper

def healthInformationResponse = context.expand('${POST healthInformation#Response}')
def slurperJson = new JsonSlurper()
def healthInformation = slurperJson.parseText(healthInformationResponse)
benefits = healthInformation.payload.benefitsGroup.benefits

//Проверка сортировки по benefitType.code
for(i=0; i<benefits.size(); i++){
	if(i==0 || i== 1 || i ==2){
		assert benefits[i].benefitType.code == "drugs"
	}
	if(i==3 || i== 4 || i ==5){
		assert benefits[i].benefitType.code == "food"
	}
	if(i==6 || i== 7 || i ==8){
		assert benefits[i].benefitType.code == "dentures"
	}
}
//Проверка сортировки внутри benefitType
for(i=0; i<benefits.size(); i++){
	if(i==0){
		assert benefits[i].benefitsCode == "722"
	}
	if(i==1 || i== 2){
		assert benefits[i].benefitsCode > "722"
	}
	if(i==3){
		assert benefits[i].benefitsCode == "748"
	}
	if(i==4 || i== 5){
		assert benefits[i].benefitsCode > "748"
	}
	if(i==6){
		assert benefits[i].benefitsCode == "612"
	}
	if(i==7 || i== 8){
		assert benefits[i].benefitsCode > "612"
	}
}

import groovy.json.JsonSlurper

def healthInformationResponse = context.expand('${POST healthInformation#Response}')
def slurperJson = new JsonSlurper()
def healthInformation = slurperJson.parseText(healthInformationResponse)
dispensary = healthInformation.payload.dispensaryGroup
//Проверка сортировки по дате
assert dispensary[0].opened == "2023-09-06"
assert dispensary[1].opened == "2022-12-20"
assert dispensary[2].opened == "2022-09-05"
