import groovy.json.JsonSlurper
import groovy.xml.XmlSlurper

def getInquiryPurposeRest = context.expand('${getInquiryPurpose#Response}')
def slurperJson = new JsonSlurper()
def restParse = slurperJson.parseText(getInquiryPurposeRest)

def getSpecialitiesInfoSOAP = context.expand('${getSpecialitiesInfo#Response}')
soapParseSpecialities = new XmlSlurper().parseText(getSpecialitiesInfoSOAP)

def GetInquiryPurposeSoap = context.expand('${GetInquiryPurpose#Response}')
soapParsePurpose = new XmlSlurper().parseText(GetInquiryPurposeSoap)

def Count =  soapParsePurpose.Body.GetInquiryPurposesResponse.Result.Patient.InquiryPurposeDictionary.size()
def SpecialitiesInResponse = soapParseSpecialities.Body.getSpecialitiesInfoResponse.medicalSpeciality.size()
def jsonPurpose = restParse.payload.size()

for (int i  = 0; i < Count; i++){
	def SpecialitiesCount = soapParsePurpose.Body.GetInquiryPurposesResponse.Result.Patient.InquiryPurposeDictionary[i].speciality.size()
	for (int j  = 0; j < SpecialitiesCount; j++){
		for (int k  = 0; k < SpecialitiesInResponse; k++){
			if ((soapParsePurpose.Body.GetInquiryPurposesResponse.Result.Patient.InquiryPurposeDictionary[i].speciality[j].code.toString() == soapParseSpecialities.Body.getSpecialitiesInfoResponse.medicalSpeciality[k].code.toString())&(soapParseSpecialities.Body.getSpecialitiesInfoResponse.medicalSpeciality[k].onlyByRefferal == "")){
				def check = 0
				def specialitiesCode = soapParsePurpose.Body.GetInquiryPurposesResponse.Result.Patient.InquiryPurposeDictionary[i].speciality[j].code.toString() 
				for (int n  = 0; n < jsonPurpose; n++){
					if (soapParsePurpose.Body.GetInquiryPurposesResponse.Result.Patient.InquiryPurposeDictionary[i].code.toInteger() ==  restParse.payload[n].inquiryPurposeCode)	{
						def SpecialitiesInRest = restParse.payload[n].specialities.size()
						for (int m  = 0; m < SpecialitiesInRest; m++){
							def ww = restParse.payload[n].specialities[m].code 
							if (restParse.payload[n].specialities[m].code == specialitiesCode.toString()){
							check = check + 1
							}
							if (m == (SpecialitiesInRest-1)){
								assert check == 1
							}
						}
					} else if (restParse.payload[n].code == "ari" || restParse.payload[n].code == "not_ari" || restParse.payload[n].code == "documents"){
							def purposeInRestPayload = restParse.payload[n].purpose.size()
							for (int l  = 0; l < purposeInRestPayload; l++){
								if (soapParsePurpose.Body.GetInquiryPurposesResponse.Result.Patient.InquiryPurposeDictionary[i].code.toInteger() ==  restParse.payload[n].purpose[l].inquiryPurposeCode)	{
									def SpecialitiesInRest = restParse.payload[n].purpose[l].specialities.size()
									for (int m  = 0; m < SpecialitiesInRest; m++){
										if (restParse.payload[n].purpose[l].specialities[m].code == specialitiesCode){
										check = check + 1
										}
										if (m == (SpecialitiesInRest-1)){
											assert check == 1
										}
									}
								}
							}
					}
				}
			}
		}
	}
}

