con:config.script:
import groovy.json.JsonSlurper
import groovy.xml.XmlSlurper

def getTicketNumberResponse = context.expand('${getTicketNumber#Response}')
def slurperJson = new JsonSlurper()
def getTicketNumberINFO = slurperJson.parseText(getTicketNumberResponse)

def getTicketByAppointmentIdResponse = context.expand('${getTicketByAppointmentId#Response}')
getTicketByAppointmentId = new XmlSlurper().parseText(getTicketByAppointmentIdResponse)

//проверка номера очереди
assert getTicketByAppointmentId.Body.getTicketByAppointmentIdResponse.ticket == getTicketNumberINFO.payload.ticketNumber
